mapper.select:
select
count(1)
from as_customs

     and id=#{id} 
     and agentId=#{agentId} 
     and agentName=#{agentName} 
     and customName like concat('%',#{customName},'%')     
     and customType=#{customType} 
     and customTypeName=#{customTypeName} 
     and siteUrl=#{siteUrl} 
     and customStatus=#{customStatus} 
     and bossName=#{bossName} 
     and cardType=#{cardType} 
     and cardTypeName=#{cardTypeName} 
     and cardNum=#{cardNum} 
     and companyTel=#{companyTel} 
     and companyFax=#{companyFax} 
     and regDatetime=#{regDatetime} 
     and country=#{country} 
     and province=#{province} 
     and city=#{city} 
     and area=#{area} 
     and companyAddress=#{companyAddress} 
     and memo=#{memo} 
     and agentCode=#{agentCode}

select

from as_customs

   and id=#{id} 
   and agentId=#{agentId} 
   and agentName=#{agentName} 
   and customName like concat('%',#{customName},'%')     
   and customType=#{customType} 
   and customTypeName=#{customTypeName} 
   and siteUrl=#{siteUrl} 
   and customStatus=#{customStatus} 
   and bossName=#{bossName} 
   and cardType=#{cardType} 
   and cardTypeName=#{cardTypeName} 
   and cardNum=#{cardNum} 
   and companyTel=#{companyTel} 
   and companyFax=#{companyFax} 
   and regDatetime=#{regDatetime} 
   and country=#{country} 
   and province=#{province} 
   and city=#{city} 
   and area=#{area} 
   and companyAddress=#{companyAddress} 
   and memo=#{memo} 
   and agentCode=#{agentCode} 

order by id desc

  limit #{pageStartNum},#{pageSize};

mapper.delete:
delete from as_customs

   and id=#{id} 
   and agentId=#{agentId} 
   and agentName=#{agentName} 
   and customName=#{customName} 
   and customType=#{customType} 
   and customTypeName=#{customTypeName} 
   and siteUrl=#{siteUrl} 
   and customStatus=#{customStatus} 
   and bossName=#{bossName} 
   and cardType=#{cardType} 
   and cardTypeName=#{cardTypeName} 
   and cardNum=#{cardNum} 
   and companyTel=#{companyTel} 
   and companyFax=#{companyFax} 
   and regDatetime=#{regDatetime} 
   and country=#{country} 
   and province=#{province} 
   and city=#{city} 
   and area=#{area} 
   and companyAddress=#{companyAddress} 
   and memo=#{memo} 
   and agentCode=#{agentCode}

mapper.update:
update as_customs
agentId=#{agentId}, agentName=#{agentName} , customName=#{customName}, customType=#{customType} , customTypeName=#{customTypeName}, siteUrl=#{siteUrl}, customStatus=#{customStatus} , bossName=#{bossName}, cardType=#{cardType} , cardTypeName=#{cardTypeName}, cardNum=#{cardNum} , companyTel=#{companyTel} , companyFax=#{companyFax} , regDatetime=#{regDatetime} , country=#{country} , province=#{province} , city=#{city} , area=#{area}, companyAddress=#{companyAddress}, memo=#{memo} , agentCode=#{agentCode},
where id = #{id};
